
module.exports		= (function(){
	var name;
	var reference;
	var level;
	var points;
	var socket;
	var game;
	var lobby;
		
	var Player	= function(name, socket){
		this.name 		= name;
		this.reference 	= name;
		this.socket		= socket;
	};

		
	Player.prototype = function(){
		var constructor = Player;
		
		var get_name 		= function(){
			return this.name;
		};
		
		var get_reference	= function(){
			return this.reference;
		};
		
		var get_points		= function(){
			return this.points;
		};
		
		var get_socket		= function(){
			return this.socket;
		};
		
		var get_game		= function(){
			return this.game;
		};
		
		var get_lobby		= function(){
			return this.lobby;
		};
		
		var set_lobby		= function(lobby){
			this.lobby = lobby;
		};
		
		var set_game		= function(game){
			this.game = game;
		};
		
		return {
			get_name		: get_name
			,get_reference	: get_reference
			,get_points		: get_points
			,get_socket		: get_socket
			,get_game		: get_game
			,get_lobby		: get_lobby
			,set_game		: set_lobby
		};
	}();
	
	return {
		Player		: Player
	};
})();
